"""
BaseSpaceCLI
----
A set of command-line tools for working with BaseSpace.
"""

import pkg_resources

from email import message_from_string

__version__ = pkg_resources.require("basespace-cli")[0].version

__build_number__ = message_from_string(
                           pkg_resources.get_distribution("basespace-cli").get_metadata('PKG-INFO')
                 )['BuildNumber']
__build_date__ = message_from_string(
                         pkg_resources.get_distribution("basespace-cli").get_metadata('PKG-INFO')
               )['BuildDate']

__summary__ = message_from_string(
                      pkg_resources.get_distribution("basespace-cli").get_metadata('PKG-INFO')
            )['Summary']

__description__ = message_from_string(
                          pkg_resources.get_distribution("basespace-cli").get_metadata('PKG-INFO')
                )['Description']

__api_timeout__ = 60
__log_terse_level__ = 35

__console_width__ = 100

